import React, { useState, useEffect } from 'react';
import authService from '../services/authService';

const POS = () => {
  const [activeTab, setActiveTab] = useState('new');
  const [transactions, setTransactions] = useState([]);
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [barcodeSearch, setBarcodeSearch] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState('CASH');
  const [customerPaid, setCustomerPaid] = useState('');
  const [discountAmount, setDiscountAmount] = useState(0);

  const paymentMethods = [
    { value: 'CASH', label: 'Cash' },
    { value: 'CREDIT_CARD', label: 'Credit Card' },
    { value: 'DEBIT_CARD', label: 'Debit Card' },
    { value: 'CHECK', label: 'Check' },
    { value: 'FOOD_STAMPS', label: 'Food Stamps' },
    { value: 'OTHER', label: 'Other' }
  ];

  useEffect(() => {
    if (activeTab === 'new') {
      fetchProducts();
      fetchCategories();
    } else {
      fetchTransactions();
    }
  }, [activeTab]);

  const fetchTransactions = async () => {
    try {
      setLoading(true);
      const response = await authService.getApiInstance().get('/pos/transactions');
      setTransactions(response.data);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await authService.getApiInstance().get('/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await authService.getApiInstance().get('/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleBarcodeSearch = async () => {
    if (!barcodeSearch.trim()) return;

    try {
      const response = await authService.getApiInstance().get(`/products/barcode/${barcodeSearch}`);
      const product = response.data;
      addToCart(product, 1);
      setBarcodeSearch('');
    } catch (error) {
      console.error('Product not found:', error);
      alert('Product not found');
    }
  };

  const handleProductSearch = (searchTerm) => {
    if (!searchTerm.trim()) {
      setSearchResults([]);
      return;
    }

    const filtered = products.filter(product =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.barcode.includes(searchTerm)
    );
    setSearchResults(filtered.slice(0, 10)); // Limit to 10 results
  };

  const addToCart = (product, quantity = 1) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.product.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.product.id === product.id
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      } else {
        return [...prevCart, { product, quantity }];
      }
    });
  };

  const updateCartItemQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
    } else {
      setCart(prevCart =>
        prevCart.map(item =>
          item.product.id === productId
            ? { ...item, quantity: newQuantity }
            : item
        )
      );
    }
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.product.id !== productId));
  };

  const calculateSubtotal = () => {
    return cart.reduce((total, item) => total + (item.product.price * item.quantity), 0);
  };

  // Calculate tax for food stamp eligible items (no tax when paying with food stamps)
  const calculateFoodStampEligibleSubtotal = () => {
    return cart
      .filter(item => item.product.foodStampEligible)
      .reduce((total, item) => total + (item.product.price * item.quantity), 0);
  };

  // Calculate tax for non-food stamp eligible items
  const calculateNonFoodStampSubtotal = () => {
    return cart
      .filter(item => !item.product.foodStampEligible)
      .reduce((total, item) => total + (item.product.price * item.quantity), 0);
  };

  const calculateTax = () => {
    return cart.reduce((tax, item) => {
      // Find category for this product - need to match by category ID since product has category object
      const category = categories.find(cat => cat.id === item.product.category?.id);
      const taxRate = category ? category.taxRate / 100 : 0;

      // If paying with food stamps, don't apply tax to food stamp eligible items
      if (selectedPaymentMethod === 'FOOD_STAMPS' && item.product.foodStampEligible) {
        return tax; // No tax for food stamp eligible items when paying with food stamps
      }

      return tax + (item.product.price * item.quantity * taxRate);
    }, 0);
  };

  const getPaymentBreakdown = () => {
    const foodStampSubtotal = calculateFoodStampEligibleSubtotal();
    const nonFoodStampSubtotal = calculateNonFoodStampSubtotal();

    // Calculate tax for non-food stamp items
    const nonFoodStampTax = cart
      .filter(item => !item.product.foodStampEligible)
      .reduce((tax, item) => {
        const category = categories.find(cat => cat.id === item.product.category?.id);
        const taxRate = category ? category.taxRate / 100 : 0;
        return tax + (item.product.price * item.quantity * taxRate);
      }, 0);

    // Calculate tax for food stamp items (only if not paying with food stamps)
    const foodStampTax = selectedPaymentMethod === 'FOOD_STAMPS' ? 0 : cart
      .filter(item => item.product.foodStampEligible)
      .reduce((tax, item) => {
        const category = categories.find(cat => cat.id === item.product.category?.id);
        const taxRate = category ? category.taxRate / 100 : 0;
        return tax + (item.product.price * item.quantity * taxRate);
      }, 0);

    return {
      foodStampSubtotal,
      foodStampTax,
      foodStampTotal: foodStampSubtotal + foodStampTax,
      nonFoodStampSubtotal,
      nonFoodStampTax,
      nonFoodStampTotal: nonFoodStampSubtotal + nonFoodStampTax,
      totalTax: foodStampTax + nonFoodStampTax,
      grandTotal: foodStampSubtotal + foodStampTax + nonFoodStampSubtotal + nonFoodStampTax - discountAmount
    };
  };

  const calculateTotal = () => {
    const breakdown = getPaymentBreakdown();
    return breakdown.grandTotal;
  };

  const calculateChange = () => {
    const paid = parseFloat(customerPaid) || 0;
    const total = calculateTotal();
    return Math.max(0, paid - total);
  };

  const handleCompleteTransaction = async () => {
    if (cart.length === 0) {
      alert('Cart is empty');
      return;
    }

    const total = calculateTotal();
    const paid = parseFloat(customerPaid) || 0;

    if (selectedPaymentMethod === 'CASH' && paid < total) {
      alert('Insufficient payment amount');
      return;
    }

    try {
      const currentUser = authService.getCurrentUser();
      const transactionItems = cart.map(item => ({
        product: { id: item.product.id },
        quantity: item.quantity,
        price: item.product.price,
        subtotal: item.product.price * item.quantity
      }));

      const payload = {
        cashier: { id: currentUser.id },
        paymentMethod: selectedPaymentMethod,
        subtotal: calculateSubtotal(),
        taxAmount: calculateTax(),
        discountAmount: discountAmount,
        total: total,
        status: 'COMPLETED',
        items: transactionItems
      };

      await authService.getApiInstance().post('/pos/transactions', payload);

      // Clear cart and reset form
      setCart([]);
      setCustomerPaid('');
      setDiscountAmount(0);
      setSelectedPaymentMethod('CASH');

      alert('Transaction completed successfully!');
    } catch (error) {
      console.error('Error completing transaction:', error);
      alert('Error completing transaction');
    }
  };

  const handleVoidTransaction = async (transactionId) => {
    if (window.confirm('Are you sure you want to void this transaction?')) {
      try {
        await authService.getApiInstance().put(`/pos/transactions/${transactionId}/void`);
        fetchTransactions();
      } catch (error) {
        console.error('Error voiding transaction:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const formatTransactionStatus = (status) => {
    const statusMap = {
      'COMPLETED': { label: 'Completed', color: 'bg-green-100 text-green-800' },
      'PENDING': { label: 'Pending', color: 'bg-yellow-100 text-yellow-800' },
      'VOIDED': { label: 'Voided', color: 'bg-red-100 text-red-800' }
    };
    return statusMap[status] || { label: status, color: 'bg-gray-100 text-gray-800' };
  };

  if (loading) {
    return (
      <div className="p-6">
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-900">Point of Sale</h1>
      </div>

      {/* Tabs */}
      <div className="mb-4">
        <nav className="flex space-x-8">
          <button
            onClick={() => setActiveTab('new')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'new'
                ? 'border-indigo-500 text-indigo-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            New Transaction
          </button>
          <button
            onClick={() => setActiveTab('history')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'history'
                ? 'border-indigo-500 text-indigo-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            Transaction History
          </button>
        </nav>
      </div>

      {activeTab === 'new' ? (
        <div className="grid grid-cols-2 gap-6">
          {/* Product Search and Cart */}
          <div className="space-y-4">
            {/* Barcode Search */}
            <div className="bg-white rounded-lg shadow p-4">
              <h3 className="text-lg font-semibold mb-2">Barcode Scanner</h3>
              <div className="flex gap-2">
                <input
                  type="text"
                  value={barcodeSearch}
                  onChange={(e) => setBarcodeSearch(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleBarcodeSearch()}
                  placeholder="Scan or enter barcode"
                  className="flex-1 border rounded px-3 py-2"
                />
                <button
                  onClick={handleBarcodeSearch}
                  className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                  Add
                </button>
              </div>
            </div>

            {/* Product Search */}
            <div className="bg-white rounded-lg shadow p-4">
              <h3 className="text-lg font-semibold mb-2">Product Search</h3>
              <input
                type="text"
                onChange={(e) => handleProductSearch(e.target.value)}
                placeholder="Search products by name..."
                className="w-full border rounded px-3 py-2 mb-2"
              />
              {searchResults.length > 0 && (
                <div className="max-h-48 overflow-y-auto border rounded">
                  {searchResults.map(product => (
                    <div
                      key={product.id}
                      onClick={() => addToCart(product)}
                      className="p-2 hover:bg-gray-100 cursor-pointer border-b"
                    >
                      <div className="font-medium">{product.name}</div>
                      <div className="text-sm text-gray-500">
                        {product.barcode} • ${product.price.toFixed(2)}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Cart */}
            <div className="bg-white rounded-lg shadow p-4">
              <h3 className="text-lg font-semibold mb-2">Cart</h3>
              {cart.length === 0 ? (
                <p className="text-gray-500">Cart is empty</p>
              ) : (
                <div className="space-y-2">
                  {cart.map(item => (
                    <div key={item.product.id} className="flex items-center justify-between border-b pb-2">
                      <div className="flex-1">
                        <div className="font-medium">{item.product.name}</div>
                        <div className="text-sm text-gray-500">
                          ${item.product.price.toFixed(2)} each
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => updateCartItemQuantity(item.product.id, item.quantity - 1)}
                          className="bg-gray-200 text-gray-600 w-6 h-6 rounded text-sm"
                        >
                          -
                        </button>
                        <span className="w-8 text-center">{item.quantity}</span>
                        <button
                          onClick={() => updateCartItemQuantity(item.product.id, item.quantity + 1)}
                          className="bg-gray-200 text-gray-600 w-6 h-6 rounded text-sm"
                        >
                          +
                        </button>
                        <button
                          onClick={() => removeFromCart(item.product.id)}
                          className="bg-red-500 text-white w-6 h-6 rounded text-sm ml-2"
                        >
                          ×
                        </button>
                      </div>
                      <div className="text-right w-20">
                        ${(item.product.price * item.quantity).toFixed(2)}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Transaction Summary and Payment */}
          <div className="space-y-4">
            <div className="bg-white rounded-lg shadow p-4">
              <h3 className="text-lg font-semibold mb-4">Transaction Summary</h3>

              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span>${calculateSubtotal().toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Tax:</span>
                  <span>${calculateTax().toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Discount:</span>
                  <span>-${discountAmount.toFixed(2)}</span>
                </div>
                <hr />
                <div className="flex justify-between font-bold text-lg">
                  <span>Total:</span>
                  <span>${calculateTotal().toFixed(2)}</span>
                </div>
              </div>

              <div className="mt-4 space-y-3">
                <div>
                  <label className="block text-sm font-medium mb-1">Discount Amount</label>
                  <input
                    type="number"
                    step="0.01"
                    value={discountAmount}
                    onChange={(e) => setDiscountAmount(parseFloat(e.target.value) || 0)}
                    className="w-full border rounded px-3 py-2"
                    placeholder="0.00"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Payment Method</label>
                  <select
                    value={selectedPaymentMethod}
                    onChange={(e) => setSelectedPaymentMethod(e.target.value)}
                    className="w-full border rounded px-3 py-2"
                  >
                    {paymentMethods.map(method => (
                      <option key={method.value} value={method.value}>
                        {method.label}
                      </option>
                    ))}
                  </select>
                </div>

                {selectedPaymentMethod === 'CASH' && (
                  <>
                    <div>
                      <label className="block text-sm font-medium mb-1">Amount Paid</label>
                      <input
                        type="number"
                        step="0.01"
                        value={customerPaid}
                        onChange={(e) => setCustomerPaid(e.target.value)}
                        className="w-full border rounded px-3 py-2"
                        placeholder="0.00"
                      />
                    </div>
                    {customerPaid && (
                      <div className="text-lg font-semibold">
                        Change: ${calculateChange().toFixed(2)}
                      </div>
                    )}
                  </>
                )}

                <button
                  onClick={handleCompleteTransaction}
                  disabled={cart.length === 0}
                  className="w-full bg-green-500 text-white py-3 rounded font-semibold hover:bg-green-600 disabled:bg-gray-300"
                >
                  Complete Transaction
                </button>
              </div>
            </div>
          </div>
        </div>
      ) : (
        /* Transaction History */
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <table className="min-w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Transaction #
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date/Time
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Cashier
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Payment Method
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Total
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {transactions.map((transaction) => {
                const status = formatTransactionStatus(transaction.status);
                return (
                  <tr key={transaction.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      #{transaction.id}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {transaction.transactionDate ? formatDate(transaction.transactionDate) : 'N/A'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {transaction.cashier?.username || 'Unknown'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {paymentMethods.find(pm => pm.value === transaction.paymentMethod)?.label || transaction.paymentMethod}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 font-semibold">
                      ${transaction.total?.toFixed(2) || '0.00'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${status.color}`}>
                        {status.label}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      {transaction.status === 'COMPLETED' && (
                        <button
                          onClick={() => handleVoidTransaction(transaction.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Void
                        </button>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default POS;
